'use strict';

const binary =require( './binary');
const { Image } =require( './common');

module.exports = function oneRoi(str) {
  
  let mask =  binary(str)

  let roiManager = mask.getRoiManager();
  roiManager.fromMask(mask);

  let rois = roiManager.getRois({positive:true,negative:false});
  return rois[0];
};
